export class GraphGenerator {

    public static random<T1, T2>(n_vertex: number, p_connected: number, selfconn: boolean = false) {
        let graph: Graph<T1, T2> = new Graph();

        // Создаем вершины
        for (let i = 0; i < n_vertex; i++) {
            let node: Node<T1> = new Node(i.toString(), i.toString(), "");
            graph.addNode(node);
        }

        // Создаем две компоненты связности
        let component1: Node<T1>[] = [];
        let component2: Node<T1>[] = [];

        // Случайным образом распределяем вершины по компонентам
        graph.nodes.forEach(node => {
            if (Math.random() < 0.5) {
                component1.push(node);
            } else {
                component2.push(node);
            }
        });

        // Функция для добавления ребер внутри компоненты
        function addEdges(nodes: Node<T1>[]) {
            let edges_ids: number[] = [0];
            nodes.forEach(node1 => {
                nodes.forEach(node2 => {
                    if (node1.id !== node2.id) {
                        let prob = Math.random();
                        if (p_connected > prob) {
                            let index = 1;
                            if (edges_ids && edges_ids.at(-1)) {
                                index = edges_ids[edges_ids.length - 1] + 1;
                            }
                            edges_ids.push(index);
                            let edge: Edge<T1, T2> = new Edge(index.toString(), node1, node2, "", "0");
                            graph.addEdge(edge);
                        }
                    }
                });
            });
        }

        // Добавляем ребра в обе компоненты
        addEdges(component1);
        addEdges(component2);

        return graph;
    }
}
